def simple(input_list, static):
    result = []
    resultx = []
    count = 1 
    input_list = [char.lower() for char in input_list]
    for i in range(1, len(input_list)):  # Start from 1 to avoid index error
        if input_list[i] == input_list[i - 1]:
            count += 1
        else:
            result.append(input_list[i - 1])
            resultx.append(float(count * static))
            count = 1 
    # Fix for the last element
    result.append(input_list[-1])
    resultx.append(float(count * static))

    return result, resultx

# Example usage:
static = 0.001640625
input_sequence = ""
output_list, out = simple(input_sequence, static)
print(output_list, out)
